mapper.select:
  		select count(*)
		from tbl_employees
		where pk_emp_no = #{pk_emp_no}

  		select count(*)
		from tbl_employees
		where uq_email = #{uq_email}

select count(*)
from tbl_commute
where fk_emp_no = #{fk_emp_no} and regdate = #{regdate}

select nvl(in_time, '미등록') AS in_time, nvl(out_time, '미등록') AS out_time
from tbl_commute
where fk_emp_no = #{fk_emp_no} and regdate = #{regdate}

select count(*)
from tbl_commute
where fk_emp_no = #{fk_emp_no} and regdate = #{regdate}
