mapper.select:
select pk_mail_num, fk_senduser_num, sendempname, subject, reg_date, filename from ( select row_number() over(order by pk_mail_num desc) AS rno, pk_mail_num, fk_senduser_num, sendempname, subject , to_char(reg_date,'yyyy-mm-dd hh24:mi:ss') as reg_date , filename from tbl_mail where fk_receiveuser_num = #{fk_receiveuser_num} and del_status = 0 and reservation_status = 0
and lower(${searchType}) like '%' || lower(#{searchWord}) || '%'
) V where rno between #{startRno} and #{endRno}

select pk_mail_num, fk_receiveuser_num, recempname, subject, reg_date, filename from ( select row_number() over(order by pk_mail_num desc) AS rno, pk_mail_num, fk_receiveuser_num, recempname, subject , to_char(reg_date,'yyyy-mm-dd hh24:mi:ss') as reg_date , filename from tbl_mail where fk_senduser_num = #{fk_senduser_num} and del_status = 0 and reservation_status = 0
and lower(${searchType}) like '%' || lower(#{searchWord}) || '%'
) V where rno between #{startRno} and #{endRno}

select pk_mail_num, fk_senduser_num, fk_receiveuser_num, recemail, sendemail, recempname, sendempname, subject, content, reg_date , filename, orgfilename, filesize from ( select pk_mail_num, fk_senduser_num, fk_receiveuser_num, recemail, sendemail, recempname, sendempname, subject, content , to_char(reg_date, 'yyyy-mm-dd hh24:mi:ss') as reg_date , fileName, orgFilename, fileSize from tbl_mail
where lower(${searchType}) like '%'|| lower(#{searchWord}) || '%'
) V where V.pk_mail_num = #{pk_mail_num}

select pk_mail_num, fk_senduser_num, fk_receiveuser_num, recemail, sendemail, recempname, sendempname, subject, content, reg_date , filename, orgfilename, filesize from ( select pk_mail_num, fk_senduser_num, fk_receiveuser_num, recemail, sendemail, recempname, sendempname, subject, content , to_char(reg_date, 'yyyy-mm-dd hh24:mi:ss') as reg_date , fileName, orgFilename, fileSize from tbl_mail
where lower(${searchType}) like '%'|| lower(#{searchWord}) || '%'
) V where V.pk_mail_num = #{pk_mail_num}

select emp_name, pk_emp_no
from tbl_employees
where uq_email = #{uq_email}

select pk_mail_num, fk_senduser_num, sendempname, subject, reg_date, filename from ( select row_number() over(order by pk_mail_num desc) AS rno, pk_mail_num, fk_senduser_num, sendempname, subject , to_char(reg_date,'yyyy-mm-dd hh24:mi:ss') as reg_date , filename from tbl_mail where (fk_receiveuser_num = #{fk_receiveuser_num} or fk_senduser_num = #{fk_receiveuser_num} ) and del_status = 1
and lower(${searchType}) like '%' || lower(#{searchWord}) || '%'
) V where rno between #{startRno} and #{endRno}

select pk_mail_num, fk_senduser_num, fk_receiveuser_num, recemail, sendemail, recempname, sendempname, subject, content, reg_date , filename, orgfilename, filesize from ( select pk_mail_num, fk_senduser_num, fk_receiveuser_num, recemail, sendemail, recempname, sendempname, subject, content , to_char(reg_date, 'yyyy-mm-dd hh24:mi:ss') as reg_date , fileName, orgFilename, fileSize from tbl_mail
where lower(${searchType}) like '%'|| lower(#{searchWord}) || '%'
) V where V.pk_mail_num = #{pk_mail_num}

select pk_mail_num, fk_receiveuser_num, recempname, subject, reg_date, filename, reservation_date from ( select row_number() over(order by pk_mail_num desc) AS rno, pk_mail_num, fk_receiveuser_num, recempname, subject , to_char(reg_date,'yyyy-mm-dd hh24:mi:ss') as reg_date , filename, to_char(reservation_date, 'yyyy-mm-dd hh24:mi') as reservation_date from tbl_mail where fk_senduser_num = #{fk_senduser_num} and del_status = 0 and reservation_status = 1
and lower(${searchType}) like '%' || lower(#{searchWord}) || '%'
order by reservation_date desc ) V where rno between #{startRno} and #{endRno}

select pk_mail_num, fk_senduser_num, fk_receiveuser_num, recemail, sendemail , recempname, sendempname, subject, content, reg_date, reservation_date , filename, orgfilename, filesize from ( select pk_mail_num, fk_senduser_num, fk_receiveuser_num, recemail, sendemail, recempname, sendempname, subject, content , to_char(reg_date, 'yyyy-mm-dd hh24:mi:ss') as reg_date , to_char(reservation_date, 'yyyy-mm-dd hh24:mi') as reservation_date , fileName, orgFilename, fileSize from tbl_mail
where lower(${searchType}) like '%'|| lower(#{searchWord}) || '%'
) V where V.pk_mail_num = #{pk_mail_num}
