mapper.select:
select pk_mail_num, fk_senduser_num, empname, subject, reg_date from ( select row_number() over(order by pk_mail_num desc) AS rno, pk_mail_num, fk_senduser_num, empname, subject , to_char(reg_date,'yyyy-mm-dd hh24:mi:ss') as reg_date from tbl_mail
where lower(${searchType}) like '%'|| lower(#{searchWord}) || '%'
) V where rno between #{startRno} and #{endRno}

select pk_mail_num, fk_senduser_num, fk_receiveuser_num, empname, email, subject, content, reg_date , filename, orgfilename, filesize from ( select pk_mail_num, fk_senduser_num, fk_receiveuser_num, empname, email, subject, content , to_char(reg_date, 'yyyy-mm-dd hh24:mi:ss') as reg_date , fileName, orgFilename, fileSize from tbl_mail
where and lower(${searchType}) like '%'|| lower(#{searchWord}) || '%'
) V where V.pk_mail_num = #{pk_mail_num}
