mapper.select:
select pk_seq, fk_emp_no, user_name, subject, read_count, write_day, comment_count, content, groupno, fk_seq, depthno, filename, ko_rankname from ( select rownum AS rno, pk_seq, fk_emp_no, user_name, subject, read_count, write_day, comment_count, content, groupno, fk_seq, depthno, filename, ko_rankname from ( select B.pk_seq AS pk_seq, B.fk_emp_no AS fk_emp_no, B.user_name AS user_name, B.subject AS subject, B.read_count AS read_count, B.content AS content, to_char(B.write_day, 'yyyy-mm-dd hh24:mi:ss') AS write_day, B.comment_count AS comment_count, B.groupno AS groupno, B.fk_seq AS fk_seq, B.depthno AS depthno, B.filename AS filename, S.ko_rankname AS ko_rankname from tbl_board B join tbl_employees E ON B.user_name = E.emp_name join tbl_rank_sort S ON E.FK_RANK_ID = S.PK_RANK_NO where status = 1
and lower(${searchType}) like '%'||lower(#{searchWord}) ||'%'
start with fk_seq = 0 connect by prior pk_seq = fk_seq order siblings by groupno desc, pk_seq asc ) V )T where rno between #{startRno} and #{endRno}

select previousseq, previoussubject , pk_seq, fk_emp_no, user_name, subject, content, read_count, write_day, pw , nextseq, nextsubject , groupno, fk_seq, depthno, like_cnt , filename, org_Filename, file_size, comment_count, ko_rankname from ( select lag(pk_seq,1) over(order by pk_seq desc) AS previousseq , lag(subject,1) over(order by pk_seq desc) AS previoussubject , pk_seq, fk_emp_no, user_name, subject, content, read_count , to_char(write_day, 'yyyy-mm-dd hh24:mi:ss') as write_day , pw, like_cnt , lead(pk_seq,1) over(order by pk_seq desc) AS nextseq , lead(subject,1) over(order by pk_seq desc) AS nextsubject , groupno, fk_seq, depthno , filename, org_Filename, file_size, comment_count, S.ko_rankname AS ko_rankname from tbl_board B join tbl_employees E ON B.user_name = E.emp_name join tbl_rank_sort S ON E.FK_RANK_ID = S.PK_RANK_NO where status = 1
and lower(${searchType}) like '%'|| lower(#{searchWord}) ||'%'
) V where V.pk_seq = #{pk_seq}

mapper.update:
    update tbl_board set subject = #{subject}
	                   , content = #{content}
	where pk_seq = #{pk_seq} and pw = #{pw}

    update tbl_board set subject = #{subject}
	                   , content = #{content}
	                   , write_day = default
	                   , pw = #{pw}
	                   , status = 1
	                   , pk_seq = boardSeq.nextval
	where pk_seq = #{pk_seq}

mapper.select:
    select count (*)
	from tbl_board
	where status = 2 and fk_emp_no = #{fk_emp_no}

select ceil(count(*)/#{sizePerPage})
from tbl_comment
where status = 1 and fk_seq = #{fk_seq}

select name, content, regDate, fk_emp_no, pk_seq, fk_seq
from 
(
select row_number() over(order by pk_seq desc) AS rno, name, content, fk_emp_no, pk_seq, fk_seq, to_char(regDate, 'yyyy-mm-dd hh24:mi:ss') AS regDate
from tbl_comment
where status = 1 and fk_seq = #{fk_seq}
) V
where rno between #{startRno} and #{endRno}

mapper.update:
update tbl_board set 
like_cnt = like_cnt+1
where pk_seq = #{fk_seq}

update tbl_board set 
like_cnt = like_cnt - 1
where pk_seq = #{fk_seq}

mapper.select:
select distinct ${searchType}
select ${searchType}
from tbl_board where status = 1 and lower(${searchType}) like '%'|| lower(#{searchWord}) ||'%'
order by ${searchType} asc
order by pk_seq desc
